var Hook = require('hook.io').Hook;

var hook1 = new Hook({ 
  name: "server-hook",
});

hook1.on('hook::ready', function(){

  hook1.on('*::faker::generate::userCard::result', function(){
    
    console.log('got back some users');
    
  });

  hook1.process({
    "type": "faker",
    "event": "faker::generate::userCard"
  });

});

hook1.start();